<?phpheader("Content-Type: application/javascript");?><?php$file = fopen("../loading.html", "r") or die("Unable to open file!");$loadingHtml='';while (($line = fgets($file)) !== false) {  $line=substr($line, 0, -2);  $loadingHtml .="'$line'+";}  $loadingHtml = substr($loadingHtml, 0, -1);  fclose($file);?>//eventsloading();$(document).ready(function(){	//header positioning    $(window).hashchange( function(){        loadPageContent();    });    $(window).scroll(sticky_relocate);    sticky_relocate();    //    loadPageContent();    $("body header .toolbar #sticky a").click(function(event){        event.preventDefault();        parent.location.hash = $(this).attr("href");    });    setTimeout(function(){  removeLoading();   }, 1000);});//functions//loadingfunction loading(){  window.strHtml = <?php echo $loadingHtml; ?>;  window.objHtml=$(window.strHtml);  $("body").append(window.objHtml);}//remove loadingfunction removeLoading(){  window.objHtml.remove();}//header positioningfunction sticky_relocate() {    var window_top = $(window).scrollTop();    var div_top = $('div.title').offset().top+$('div.title').height();    if (window_top > div_top) {        $('#sticky').addClass('stick');    } else {        $('#sticky').removeClass('stick');    }}//loads content of pagefunction loadPageContent(){        var hash=window.location.hash.substring(1)==="/"||window.location.hash.substring(1)===""?"Home/default.html":window.location.hash.substring(1);        if(hash.toLowerCase().substr(0,6)==="force|"){            window.location.replace(hash.substr(6));        }else{            $("body header .toolbar #sticky a").removeClass("active");            if(!(hash.substring(0,hash.indexOf("/")).toLowerCase()==="home")){                $("body header .toolbar #sticky a").each(function(){                    if(hash.substring(0,hash.indexOf("/")).toLowerCase()===$(this).text().toString().toLowerCase()){                        $(this).addClass("active");                    }                });            }else{$("body header .toolbar #sticky a:contains('DOMOV')").addClass("active");}            if (hash.toLowerCase().indexOf("http://") >= 0||hash.toLowerCase().indexOf("https://") >= 0){                location.href=hash;            }else{                $.ajax({                    url: hash,                    dataType: "text",                    success: function(data) {                        $("#body").html(data);                    },                    error: function(xhr, status) {                        $("#body").html("Error:" + xhr.responseXML);                    }               });            }        }    }//